import UIKit
import Foundation

class HomeScreenModuleBuilder {

    static func build() -> UIViewController {
        let storyboard = UIStoryboard.init(name: "HomeScreen", bundle: nil)
        let view = storyboard.instantiateViewController(withIdentifier: "HomeScreenId") as! HomeScreenView
        let interactor = HomeScreenInteractor()
        let router = HomeScreenRouter(view: view)
        let presenter = HomeScreenPresenter(view: view, interactor: interactor, router: router)
        view.presenter = presenter
        interactor.presenter = presenter
        return view
    }

}
