import UIKit
import Foundation

class TransactionDetailsScreenModuleBuilder {

    static func build(_ date: String) -> UIViewController {
        let storyboard = UIStoryboard.init(name: "TransactionDetailsScreen", bundle: nil)
        let view = storyboard.instantiateViewController(withIdentifier: "TransactionDetailsScreenId") as! TransactionDetailsScreenView
        let interactor = TransactionDetailsScreenInteractor()
        let router = TransactionDetailsScreenRouter(view: view)
        let presenter = TransactionDetailsScreenPresenter(view: view, interactor: interactor, router: router)
        view.presenter = presenter
        interactor.presenter = presenter
        interactor.date = date
        return view
    }

}
