import UIKit
import Foundation

class TransactionScreenRouter {

    var viewController: UIViewController
    init(view: UIViewController) {
        self.viewController = view
    }

}

// MARK: - Presenter to Wireframe Interface
extension TransactionScreenRouter: TransactionScreenPresenterToRouterProtocol {
    
    func gotoNextScreen(fromVC: UIViewController, _ date: String) {
        let showTransactionDetailVC = TransactionDetailsScreenModuleBuilder.build(date)
        fromVC.navigationController?.pushViewController(showTransactionDetailVC, animated: true)
    }
}
